import React, { Fragment, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { MDBDataTableV5 } from 'mdbreact'

import MetaData from '../layouts/MetaData'
import Loader from '../layouts/Loader'
import Sidebar from './Sidebar'
import swal from 'sweetalert'
import NumberFormat from 'react-number-format'

import { useAlert } from 'react-alert'
import { useDispatch, useSelector } from 'react-redux'
import { getAdminProducts, deleteProduct, clearErrors } from '../../actions/productActions'
import { addItemToImportReceipt } from '../../actions/receiptAction'
import { DELETE_PRODUCT_RESET } from '../../constants/productConstants'

const ProductsList = ({ history }) => {

    const alert = useAlert();
    const dispatch = useDispatch();

    const { loading, error, products } = useSelector(state => state.products);
    const { error: deleteError, isDeleted } = useSelector(state => state.product)

    useEffect(() => {
        dispatch(getAdminProducts());

        if (error) {
            alert.error(error);
            dispatch(clearErrors())
        }

        if (deleteError) {
            alert.error(deleteError);
            dispatch(clearErrors())
        }

        if (isDeleted) {
            alert.success('Product deleted successfully')
            history.push('/admin/products')
            dispatch({ type: DELETE_PRODUCT_RESET })
        }

    }, [dispatch, alert, error, deleteError, isDeleted, history])

    const setProducts = () => {
        const data = {
            columns: [
                {
                    label: 'ID',
                    field: 'id',
                    sort: 'asc'
                },
                {
                    label: 'Tên',
                    field: 'name',
                    sort: 'asc'
                },
                {
                    label: 'Giá',
                    field: 'price',
                    sort: 'asc'
                },
                {
                    label: 'Số lượng',
                    field: 'stock',
                    sort: 'asc'
                },
                {
                    label: 'Thực hiện',
                    field: 'actions',
                },
            ],
            rows: []
        }

        products && products.forEach(product => {
            data.rows.push({
                id: product._id,
                name: product.name,
                price: <NumberFormat value={product.price} displayType={'text'} thousandSeparator={true} prefix={'đ '} />,
                stock: product.stock,
                actions: <Fragment>
                    <a href className="icon icon-delete" onClick={() => importToReceipt(product._id)}>
                        <i class="fas fa-plus"></i>
                    </a>
                    <Link to={`/admin/product/${product._id}`} className="icon icon-edit">
                        <i className="bx bxs-pencil" />
                    </Link>
                    <a href className="icon icon-delete" onClick={() => deleteProductHandler(product._id)}>
                        <i className='bx bxs-trash-alt' ></i>
                    </a>

                </Fragment>
            })
        })

        return data;
    }
    const importToReceipt = (id) => {
        swal({
            title: "Are you sure?",
            text: "Once deleted, you will not be able to recover this imaginary file!",
            icon: "warning",
            buttons: true,
            dangerMode: true,
        })
            .then((willDelete) => {
                if (willDelete) {
                    dispatch(addItemToImportReceipt(id, 1, 0))
                    swal("Poof! Your imaginary file has been deleted!", {
                        icon: "success",
                    })
                } else {
                    swal("Haha Nope!")
                }
            })
    }

    const deleteProductHandler = (id) => {
        swal({
            title: "Are you sure?",
            text: "Once deleted, you will not be able to recover this imaginary file!",
            icon: "warning",
            buttons: true,
            dangerMode: true,
        })
            .then((willDelete) => {
                if (willDelete) {
                    dispatch(deleteProduct(id))
                    swal("Poof! Your imaginary file has been deleted!", {
                        icon: "success",
                    })
                } else {
                    swal("Haha Nope!")
                }
            })
    }


    return (
        <Fragment>
            <MetaData title={'Admin - All Products'} />
            <div className="row admin-products">
                <div className="col-12 col-md-2">
                    <Sidebar />
                </div>

                <div className="col-12 col-md-10">
                    <Fragment>
                        <div className="users-list-header my-4">
                            <h3>Toàn bộ sản phẩm (Tổng số: {products && products.length})</h3>
                            <Link to='/admin/products/new' className="btn btn-new-user">Add New Product</Link>
                        </div>

                        {loading ? <Loader /> : (
                            <MDBDataTableV5
                                data={setProducts()}
                                className="px-3"
                                hover
                                searchTop
                                searchBottom={false}
                            />
                        )}

                    </Fragment>
                </div>
            </div>

        </Fragment>
    )
}

export default ProductsList
